func (e *SeatNameEvent) Proxy() wayland.Proxy {
	return e.proxy
}
func (i *Seat) Dispatch(msg *wayland.Message) {
	switch msg.Opcode {
	case 0:
		if i.Handler == nil {
			return
		}
		e := &SeatCapabilitiesEvent{proxy: i}
		r := wayland.NewMessageReader(i.Conn(), msg)
		e.capabilities = SeatCapability(r.ReadUint())
		if i.Handler.Handle(e) {
			return
		}
	case 1:
		if i.Handler == nil {
			return
		}
		e := &SeatNameEvent{proxy: i}
		r := wayland.NewMessageReader(i.Conn(), msg)
		e.name = r.ReadString()
		if i.Handler.Handle(e) {
			return
		}
	}
}

// Pointer : pointer input device
//
// The wl_pointer interface represents one or more input devices,
// such as mice, which control the pointer location and pointer_focus
// of a seat.
//
// The wl_pointer interface generates motion, enter and leave
// events for the surfaces that the pointer is located over,
